import React, { useState } from 'react';

const App = () => {
   //配列を返し、二つの配列を返す。
   //名前の付け方は、一つ目がxであれば、二つ目はそれを設定できる意味合いを込めてsetXとする。
  const [count, setCount] = useState(0)

  return (
    <React.Fragment>
    <div>count: {count}</div>
    <button>+1</button>
    </React.Fragment>
  );
}

export default App;
