import React, { useState } from 'react';

const App = () => {
  const initialState = {
    name: '',
    price: 1000
  }

  const [name, setName] = useState(initialState.name)
  const [price, setPrice] = useState(initialState.price)

  return (
    <>
      <p>現在の{name}は、{price}円です。</p>
    </>
  );
}

export default App;
