import React, { useReducer, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

import reducer from '../reducers'

const App = () => {

  const [state, dispatch] = useReducer(reducer, [])
  const [title, setTitle] = useState('')
  const [body, setBody] =useState('')

  const addEvent = e => {
    //reloadを防止している。このeはeventである。
    e.preventDefault()
    dispatch({
      type: 'CREATE_EVENT',
      title,
      body
    })
    console.log(state);
    setTitle('')
    setBody('')
  }

  return (
    <div className="container-fluid">
      <h4>イベント作成フォーム</h4>
      <form>
        <div className="form-group">
          <label htmlFor="formEventTitle">タイトル</label>
          {/* htmlForとidの値が同じであれば、ラベルをタップした時に、フォーカスされる。 */}
          <input className="form-control" id="formEventTitle" value={title} onChange={e => setTitle(e.target.value)}/>
        </div>

        <div className="form-group">
          <label htmlFor="formEventTitle">ボディー</label>
          <textarea className="form-control" id="formEventTitle" value={body} onChange={e => setBody(e.target.value)}/>
        </div>

        <button className="btn btn-primary" onClick={addEvent}>イベントの作成する</button>
        <button className="btn btn-danger">全てのイベントを削除する</button>
      </form>

      <h4>イベント一覧</h4>
      <table className="table table-hover">
        <thead>
          <tr>
            <th>ID</th>
            <th>タイトル</th>
            <th>ボディー</th>
          </tr>
        </thead>
        <tbody>
          {
            // mapで同じ処理を回している。そうすると同じtrがなんども生成出力されるので、一個一個を識別できない。よって識別するためのkeyをつける必要がある。
            // mapの仕様で決まっているindexを第二引数として受け取りそれを、keyとして利用する。
            state.map((event, index) => {
              //特別に関数を定義しない場合は、丸括弧で一括returnしてもいいが、関数を定義する場合は、ブラケットを開いて、関数定義とreturnを区別する必要がある。
              const id = event.id
              const handleClickDeleteButton = () =>{
                dispatch({ type: 'DELETE_EVENT', id })
              }

              return(
                <tr key={index}>
                  <td>{event.id}</td>
                  <td>{event.title}</td>
                  <td>{event.body}</td>
                  <td><button type="button" className="btn btn-danger" onClick={handleClickDeleteButton}>削除</button></td>
              </tr>
              )})
          }
        </tbody>
      </table>
    </div>
  );
}

export default App;
